\n	public function detail($slug)\r\n	{\r\n		$postDetail = $this-&gt;post-&gt;getDetailPost($slug);\r\n\r\n		$data = array(\r\n			\'posts\'			=&gt; $this-&gt;post-&gt;getAllData(),\r\n			\'title\'			=&gt; $postDetail[\'title\'],\r\n			\'content\' 		=&gt; $postDetail[\'content\'],\r\n			\'slug\' 			=&gt; $postDetail[\'slug\'],\r\n			\'date_create\'	=&gt; $postDetail[\'date_create\'],\r\n		);\r\n\r\n		$this-&gt;load-&gt;view(\'templates/v_header\', $data);\r\n		$this-&gt;load-&gt;view(\'v_detail\', $data);\r\n		$this-&gt;load-&gt;view(\'templates/v_footer\', $data);\r\n	}\r\n\r\n\r\n	public function add()\r\n	{\r\n		$data = array(\r\n			\'title\' =&gt; \'Add Post\',\r\n		);\r\n\r\n		$this-&gt;load-&gt;view(\'templates/v_header\', $data);\r\n		$this-&gt;load-&gt;view(\'v_add\');\r\n		$this-&gt;load-&gt;view(\'templates/v_footer\');\r\n	}\r\n\r\n\r\n	public function save()\r\n	{\r\n		// validate input\r\n		//                                name     teks               \r\n		$this-&gt;form_validation-&gt;set_rules(\'title\', \'Title\', \'required|is_unique[posts.title]\');\r\n		$this-&gt;form_validation-&gt;set_rules(\'content\', \'Content\', \'required\');\r\n\r\n		// validate image\r\n		if (empty($_FILES[\'thumbnail\'][\'name\'])) {\r\n			$this-&gt;form_validation-&gt;set_rules(\'thumbnail\', \'Thumbnail\', \'required\');\r\n		}\r\n\r\n		if ($this-&gt;form_validation-&gt;run() == FALSE) {\r\n			$this-&gt;add();\r\n		} else {\r\n\r\n			$config[\'upload_path\']		= \'./assets/img/\'; //path folder\r\n			$config[\'allowed_types\'] 	= \'gif|jpg|png|jpeg|bmp\';\r\n			$config[\'encrypt_name\']		= TRUE;\r\n\r\n			$this-&gt;upload-&gt;initialize($config);\r\n\r\n			if (!empty($_FILES[\'thumbnail\'][\'name\'])) {\r\n				// if user  uploading image\r\n\r\n				if ($this-&gt;upload-&gt;do_upload(\'thumbnail\')) {\r\n					$img = $this-&gt;upload-&gt;data();\r\n\r\n					//compress Image\r\n					$this-&gt;_resizeImage($img[\'file_name\']);\r\n\r\n					$title = $this-&gt;input-&gt;post(\'title\');\r\n					$content = $this-&gt;input-&gt;post(\'content\');\r\n					$description = $this-&gt;input-&gt;post(\'description\');\r\n					$thumbnail = $img[\'file_name\'];\r\n\r\n					$data = array(\r\n						\'title\'			=&gt; $title,\r\n						// built in helper CI3\r\n						\'slug\'			=&gt; url_title($title),\r\n						\'thumbnail\' 	=&gt; $thumbnail,\r\n						\'content\' 		=&gt; $content,\r\n						\'date_create\'	=&gt; date(\"Y-m-d H:i:s\"),\r\n						\'last_update\'	=&gt; date(\"Y-m-d H:i:s\"),\r\n					);\r\n\r\n					// insert data with model\r\n					$this-&gt;post-&gt;insertPost(\'posts\', $data);\r\n\r\n					$this-&gt;session-&gt;set_flashdata(\'message\', \'post added!\');\r\n					$this-&gt;add();\r\n				} else {\r\n					// if uploading image error\r\n					// show error\r\n					$this-&gt;session-&gt;set_flashdata(\'message\', $this-&gt;upload-&gt;display_errors());\r\n					$this-&gt;add();\r\n				}\r\n			} else {\r\n				// if user not uploading image\r\n				$this-&gt;session-&gt;set_flashdata(\'message\', \'image is empty or type of image not allowed!\');\r\n				$this-&gt;add();\r\n			}\r\n		}\r\n	}\r\n\r\n\r\n	function _resizeImage($file_name)\r\n	{\r\n		// Image resizing config\r\n		$config = array(\r\n			\'image_library\' =&gt; \'GD2\',\r\n			\'source_image\'  =&gt; \'./assets/img/\' . $file_name,\r\n			\'maintain_ratio\' =&gt; FALSE,\r\n			\'width\'         =&gt; 600,\r\n			\'height\'        =&gt; 400,\r\n			\'new_image\'     =&gt; \'./assets/img/resize/\' . $file_name\r\n		);\r\n\r\n		// load config (built in liblary CI3)\r\n		$this-&gt;load-&gt;library(\'image_lib\', $config);\r\n\r\n		$this-&gt;image_lib-&gt;initialize($config);\r\n		if (!$this-&gt;image_lib-&gt;resize()) {\r\n			return false;\r\n		}\r\n		$this-&gt;image_lib-&gt;clear();\r\n	}\r\n\r\n\r\n	public function edit($slug)\r\n	{\r\n		$postDetail = $this-&gt;post-&gt;getDetailPost($slug);\r\n\r\n		$data = array(\r\n			\'title\'			=&gt; $postDetail[\'title\'],\r\n			\'content\' 		=&gt; $postDetail[\'content\'],\r\n			\'date_create\'	=&gt; $postDetail[\'date_create\'],\r\n			\'id\'			=&gt; $postDetail[\'id\'],\r\n			\'slug\'			=&gt; $postDetail[\'slug\'],\r\n			\'thumbnail\'		=&gt; $postDetail[\'thumbnail\'],\r\n		);\r\n\r\n		$this-&gt;load-&gt;view(\'templates/v_header\', $data);\r\n		$this-&gt;load-&gt;view(\'v_edit\', $data);\r\n		$this-&gt;load-&gt;view(\'templates/v_footer\', $data);\r\n	}\r\n\r\n\r\n	public function update()\r\n	{\r\n		$title 			= $this-&gt;input-&gt;post(\'title\');\r\n		$id				= $this-&gt;input-&gt;post(\'id\');\r\n		$slug			= $this-&gt;input-&gt;post(\'slug\');\r\n		$content 		= $this-&gt;input-&gt;post(\'content\');\r\n		$old_image		= $this-&gt;input-&gt;post(\'old_image\');\r\n		$last_update 	= date(\"Y-m-d H:i:s\");\r\n\r\n		$where = array(\'id\' =&gt; $id);\r\n\r\n		$no_update_image = array(\r\n			\'title\'			=&gt; $title,\r\n			\'content\'		=&gt; $content,\r\n			\'last_update\'	=&gt; $last_update,\r\n		);\r\n\r\n		$this-&gt;form_validation-&gt;set_rules(\'title\', \'Title\', \'required\');\r\n		$this-&gt;form_validation-&gt;set_rules(\'content\', \'Content\', \'required\');\r\n\r\n\r\n		if ($this-&gt;form_validation-&gt;run() == FALSE) {\r\n			$this-&gt;edit($slug);\r\n		} else {\r\n\r\n			$config[\'upload_path\']		= \'./assets/img/\'; //path folder\r\n			$config[\'allowed_types\'] 	= \'gif|jpg|png|jpeg|bmp\';\r\n			$config[\'encrypt_name\']		= TRUE;\r\n\r\n			$this-&gt;upload-&gt;initialize($config);\r\n\r\n			if (!empty($_FILES[\'thumbnail\'][\'name\'])) {\r\n\r\n				if ($this-&gt;upload-&gt;do_upload(\'thumbnail\')) {\r\n					$img = $this-&gt;upload-&gt;data();\r\n\r\n					//Compress Image\r\n					$this-&gt;_resizeImage($img[\'file_name\']);\r\n\r\n					$thumbnail = $img[\'file_name\'];\r\n\r\n					$data = array(\r\n						\'title\'			=&gt; $title,\r\n						\'content\'		=&gt; $content,\r\n						\'last_update\'	=&gt; $last_update,\r\n						\'thumbnail\'   	=&gt; $thumbnail,\r\n					);\r\n\r\n					$this-&gt;db-&gt;update(\'posts\', $data, $where);\r\n\r\n					// BUAT HAPUS FILE GAMBAR SEBELUMNYA\r\n					$filename = explode(\".\", $old_image)[0];\r\n					array_map(\'unlink\', glob(FCPATH . \"assets/img/$filename.*\"));\r\n					array_map(\'unlink\', glob(FCPATH . \"assets/img/resize/$filename.*\"));\r\n\r\n					$this-&gt;session-&gt;set_flashdata(\'message\', \'update Succesfully!\');\r\n					redirect(\'post/detail/\' . $slug);\r\n				} else {\r\n					echo $this-&gt;upload-&gt;display_errors();\r\n				}\r\n			} else {\r\n				$this-&gt;db-&gt;update(\'posts\', $no_update_image, $where);\r\n				$this-&gt;session-&gt;set_flashdata(\'message\', \'update Succesfully!\');\r\n				redirect(\'post/detail/\' . $slug);\r\n			}\r\n		}\r\n	}\r\n}\r\n</code></pre>\r\n\r\n<p>Aliquam leo lorem, elementum gravida laoreet ut, malesuada ut neque. Quisque dictum sollicitudin nunc, et placerat nisi facilisis in. Aenean mattis libero eget congue sollicitudin. Fusce quis neque et turpis tristique rutrum. Vestibulum condimentum iaculis auctor. Integer finibus ut dui nec iaculis. Etiam pulvinar interdum tristique. Proin lobortis imperdiet neque ut pharetra. In ornare maximus augue eget mollis.</p>\r\n\r\n<p>Praesent rutrum finibus libero, id vestibulum leo accumsan in. Nulla aliquam lacinia nunc, sed iaculis velit semper eu. Vestibulum lacinia malesuada nibh. Orci varius natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus. Nam ullamcorper diam sed nulla mattis porta. Fusce pulvinar semper sem vitae eleifend. Quisque sit amet varius sapien. Vivamus imperdiet quam dui, sed congue mi sollicitudin et. Pellentesque nec ipsum convallis, fringilla nibh et, luctus felis. Phasellus nec risus at est laoreet mattis. Etiam lacinia consectetur neque nec scelerisque.</p>\r\n\r\n<p>Sed dolor mi, euismod ac dignissim a, aliquet et nisi. Integer dictum convallis lectus. Curabitur varius, turpis in posuere auctor, quam velit varius magna, eu pretium neque eros ut mi. Etiam elementum enim sit amet erat euismo